fn day_one_part_one(input: &str){
    let mut counter: i32 = 0;

    for item in input.chars() {
        
        if(item =='('){
            counter += 1;
        }
        if(item ==')'){
            counter -= 1;
        }
    }

    println!("Day 1 Part 1: {}", counter);
}

fn day_one_part_two(input: &str){
    let mut counter: i32 = 0;
    let mut relevantStepNumber = 1;
    let mut basementReached = true;

    
    while basementReached {
        for item in input.chars() {
        
            if(item =='('){
                counter += 1;
                relevantStepNumber += 1;
            }
            if(item ==')'){
                counter -= 1;
                relevantStepNumber += 1;
                if(counter == -1){
                    basementReached = false;
                }
            }
        }   
    }
    println!("Day 1 Part 2: {}", relevantStepNumber);

}